select scd.df_acl_scenario_detail_uid, original_detail_id, 'ACL' product_family, ub.name usage_batch_name, s.period_end_date, wr_wrk_inst, system_title, print_details.rh_account_number rh_account_number_print, print_details.rh_name rh_name_print, digital_details.rh_account_number rh_account_number_digital, digital_details.rh_name rh_name_digital, scd.period_end_date usage_period, usage_age_weight, scd.detail_licensee_class_id, dlc.description detail_licensee_class_name, alc.aggregate_licensee_class_id, alc.description aggregate_licensee_class_name, survey_country, reported_type_of_use, usage_quantity number_of_copies, weighted_copies, scd.publication_type_uid, pt.name pub_type_name, pub_type_weight, price, price_flag, content, content_flag, content_unit_price, content_unit_price_flag, print_details.value_share value_share_print, print_details.volume_share volume_share_print, print_details.detail_share detail_share_print, coalesce(print_details.net_amount, 0) net_amount_print, digital_details.value_share value_share_digital, digital_details.volume_share volume_share_digital, digital_details.detail_share detail_share_digital, coalesce(digital_details.net_amount, 0) net_amount_digital, coalesce(print_details.net_amount, 0) + coalesce(digital_details.net_amount, 0) combined_net_amount from ${schema}.df_acl_scenario s join ${schema}.df_acl_usage_batch ub on s.df_acl_usage_batch_uid = ub.df_acl_usage_batch_uid join ${schema}.df_acl_scenario_detail scd on s.df_acl_scenario_uid = scd.df_acl_scenario_uid left join (
) as print_details on scd.df_acl_scenario_detail_uid = print_details.df_acl_scenario_detail_uid left join (
) as digital_details on scd.df_acl_scenario_detail_uid = digital_details.df_acl_scenario_detail_uid join ${schema}.df_publication_type pt on scd.publication_type_uid = pt.df_publication_type_uid join ${schema}.df_detail_licensee_class dlc on scd.detail_licensee_class_id = dlc.detail_licensee_class_id join ${schema}.df_acl_scenario_licensee_class slc on dlc.detail_licensee_class_id = slc.detail_licensee_class_id and slc.df_acl_scenario_uid = #{scenarioId} join ${schema}.df_aggregate_licensee_class alc on slc.aggregate_licensee_class_id = alc.aggregate_licensee_class_id

select count(1) from ${schema}.df_acl_scenario s join ${schema}.df_acl_usage_batch ub on s.df_acl_usage_batch_uid = ub.df_acl_usage_batch_uid join ${schema}.df_acl_scenario_detail scd on s.df_acl_scenario_uid = scd.df_acl_scenario_uid left join (
) as print_details on scd.df_acl_scenario_detail_uid = print_details.df_acl_scenario_detail_uid left join (
) as digital_details on scd.df_acl_scenario_detail_uid = digital_details.df_acl_scenario_detail_uid join ${schema}.df_publication_type pt on scd.publication_type_uid = pt.df_publication_type_uid join ${schema}.df_detail_licensee_class dlc on scd.detail_licensee_class_id = dlc.detail_licensee_class_id join ${schema}.df_acl_scenario_licensee_class slc on dlc.detail_licensee_class_id = slc.detail_licensee_class_id and slc.df_acl_scenario_uid = #{scenarioId} join ${schema}.df_aggregate_licensee_class alc on slc.aggregate_licensee_class_id = alc.aggregate_licensee_class_id

