mapper.insert:
insert into ${schema}.df_acl_scenario_detail ( df_acl_scenario_detail_uid, df_acl_scenario_uid, period_end_date, original_detail_id, wr_wrk_inst, system_title, detail_licensee_class_id, publication_type_uid, content_unit_price, usage_quantity, usage_age_weight, survey_country, weighted_copies,
) select distinct on (u.df_acl_usage_uid) uuid_generate_v4() df_acl_scenario_detail_uid, #{scenario.id} df_acl_scenario_uid, u.period period_end_date, original_detail_id, u.wr_wrk_inst, u.system_title, detail_licensee_class_id, publication_type_uid, content_unit_price, quantity, uaw.weight usage_age_weight, survey_country, uaw.weight * quantity weighted_copies,
from ${schema}.df_acl_usage u join ${schema}.df_acl_grant_detail gd on gd.df_acl_grant_set_uid = #{scenario.grantSetId} and gd.wr_wrk_inst = u.wr_wrk_inst and gd.grant_status = 'GRANT' and gd.is_eligible = true join ${schema}.df_acl_scenario_usage_age_weight uaw on uaw.df_acl_scenario_uid = #{scenario.id} and uaw.period_prior = (
) where df_acl_usage_batch_uid = #{scenario.usageBatchId} and uaw.weight > 0 and quantity < 2000

insert into ${schema}.df_acl_share_detail ( df_acl_share_detail_uid, df_acl_scenario_uid, df_acl_scenario_detail_uid, type_of_use, rh_account_number, aggregate_licensee_class_id, volume_weight, value_weight,
) select uuid_generate_v4() df_acl_share_detail_uid, #{scenario.id} df_acl_scenario_uid, sd.df_acl_scenario_detail_uid, gd.type_of_use, gd.rh_account_number, slc.aggregate_licensee_class_id, usage_quantity * usage_age_weight volume_weight, usage_quantity * usage_age_weight * content_unit_price * pub_type_weight value_weight,
from ${schema}.df_acl_scenario_detail sd join ${schema}.df_acl_scenario_licensee_class slc on sd.df_acl_scenario_uid = slc.df_acl_scenario_uid and sd.detail_licensee_class_id = slc.detail_licensee_class_id join ${schema}.df_acl_grant_detail gd on gd.df_acl_grant_set_uid = #{scenario.grantSetId} and gd.wr_wrk_inst = sd.wr_wrk_inst and gd.grant_status = 'GRANT' and gd.is_eligible = true where sd.df_acl_scenario_uid = #{scenario.id}

mapper.delete:
delete from ${schema}.df_acl_share_detail
where df_acl_scenario_uid = #{scenarioId}
    and (gross_amount is null or gross_amount = 0)

delete from ${schema}.df_acl_scenario_detail
where df_acl_scenario_uid = #{scenarioId}
    and df_acl_scenario_detail_uid not in (
        select df_acl_scenario_detail_uid
        from ${schema}.df_acl_share_detail
        where df_acl_scenario_uid = #{scenarioId}
    )

mapper.select:
select shd.rh_account_number, r.name rh_name, sum(case when shd.type_of_use = 'PRINT' then coalesce(shd.gross_amount, 0) else 0 end) as gross_total_print, sum(case when shd.type_of_use = 'PRINT' then coalesce(shd.service_fee_amount, 0) else 0 end) as service_fee_total_print, sum(case when shd.type_of_use = 'PRINT' then coalesce(shd.net_amount, 0) else 0 end) as net_total_print, sum(case when shd.type_of_use = 'DIGITAL' then coalesce(shd.gross_amount, 0) else 0 end) as gross_total_digital, sum(case when shd.type_of_use = 'DIGITAL' then coalesce(shd.service_fee_amount, 0) else 0 end) as service_fee_total_digital, sum(case when shd.type_of_use = 'DIGITAL' then coalesce(shd.net_amount, 0) else 0 end) as net_total_digital, count(distinct system_title) number_of_titles, count(distinct aggregate_licensee_class_id) number_of_agg_lc_classes, s.license_type from ${schema}.df_acl_scenario s join ${schema}.df_acl_scenario_detail scd on s.df_acl_scenario_uid = scd.df_acl_scenario_uid join ${schema}.df_acl_share_detail shd on scd.df_acl_scenario_detail_uid = shd.df_acl_scenario_detail_uid left join ${schema}.df_rightsholder r on shd.rh_account_number = r.rh_account_number where s.df_acl_scenario_uid = #{scenarioId}
and
group by shd.rh_account_number, rh_name, s.license_type


