mapper.sql:
where

        ufas.market ilike '%' || #{market} || '%'

    and ufas.market_period_from <= #{marketPeriodTo}
    and ufas.market_period_to >= #{marketPeriodFrom}
    and status_ind = #{status}
    and coalesce(wc.classification, '') != #{excludeClassification}

mapper.update:
with net_amount_totals as ( select sum(net_amount) net_amount_total from ${schema}.df_usage u where df_scenario_uid = #{scenarioId} ),
gt_factor as ( select ((select net_amount_total from net_amount_totals) + (s.nts_fields->>'post_service_fee_amount')::numeric) / (select net_amount_total from net_amount_totals) net_amount_gt_factor from ${schema}.df_scenario s where df_scenario_uid = #{scenarioId} )
update ${schema}.df_usage u set net_amount = u.net_amount * net_amount_gt_factor, gross_amount = u.net_amount * net_amount_gt_factor + u.service_fee_amount from gt_factor where u.df_scenario_uid = #{scenarioId}
