mapper.select:
select nested.rh_account_number, rh.name as rh_name, sum(nested.gross_amount) as gross_amount, sum(nested.net_amount) as net_amount, sum(nested.service_fee_amount) as service_fee_amount, sum(nested.item_bank_net_amount) as item_bank_net_amount, sum(nested.usage_detail_net_amount) as usage_detail_net_amount, sum(nested.count_of_passages) as count_of_passages from ( select u.rh_account_number, sum(u.gross_amount) as gross_amount, sum(u.net_amount) as net_amount, sum(u.service_fee_amount) as service_fee_amount, case when us.detail_type = #{itemBankType} then sum(u.net_amount) else 0 end item_bank_net_amount, case when us.detail_type = #{usageDetailType} then sum(u.net_amount) else 0 end usage_detail_net_amount, case when us.detail_type = #{itemBankType} then count(1) else 0 end count_of_passages from ${schema}.df_usage u join ${schema}.df_usage_sal us on u.df_usage_uid = us.df_usage_sal_uid where u.product_family = #{productFamily} and df_scenario_uid in
#{scenarioId}
group by u.rh_account_number, us.detail_type ) as nested left join ${schema}.df_rightsholder rh on rh.rh_account_number = nested.rh_account_number group by nested.rh_account_number, rh.name order by nested.rh_account_number

select u.rh_account_number, rh.name rh_name, u.wr_wrk_inst, u.system_title, sum(u.gross_amount) as gross_amount, sum(u.service_fee_amount) as service_fee_amount, sum(u.net_amount) as net_amount from ${schema}.df_usage u join ${schema}.df_usage_sal usal on u.df_usage_uid = usal.df_usage_sal_uid left join ${schema}.df_rightsholder rh on rh.rh_account_number = u.rh_account_number where u.product_family = #{productFamily} and u.df_scenario_uid in
#{scenarioId}
group by u.rh_account_number, rh.name, u.wr_wrk_inst, u.system_title order by u.rh_account_number, u.wr_wrk_inst

select reportInfo.rro_account_number, rh.name rro_name, reportInfo.payment_date, reportInfo.usages_gross_amount, reportInfo.usages_gross_amount * reportInfo.estimated_service_fee estimated_service_fee_amount, reportInfo.usages_gross_amount - (reportInfo.usages_gross_amount * reportInfo.estimated_service_fee) estimated_net_amount, reportInfo.estimated_service_fee, reportInfo.withdrawn_gross_amount from ( select batch.rro_account_number, batch.payment_date, coalesce(sum(u.gross_amount), 0) usages_gross_amount, coalesce(fee.estimated_service_fee, #{defaultEstimatedServiceFee}) estimated_service_fee, coalesce(sum(u.withdrawn_gross_amount), 0) withdrawn_gross_amount from ${schema}.df_usage_batch batch left join ${schema}.df_rro_estimated_service_fee_percentage fee on fee.rro_account_number = batch.rro_account_number left join ( select df_usage_batch_uid, gross_amount, case when status_ind in
#{withdrawnStatus}
then gross_amount else 0 end withdrawn_gross_amount from ${schema}.df_usage) u on u.df_usage_batch_uid = batch.df_usage_batch_uid where batch.payment_date <= #{paymentDate} and batch.product_family in
#{productFamily}
group by payment_date, batch.rro_account_number, fee.estimated_service_fee ) as reportInfo left join ${schema}.df_rightsholder rh on rh.rh_account_number = reportInfo.rro_account_number where reportInfo.usages_gross_amount != 0 order by reportInfo.rro_account_number, reportInfo.payment_date

select report.rro_account_number, rro.name rro_name, report.name batch_name, report.payment_date, report.market, sum(report.market_total_amount) market_total_amount, report.total_payment_amount from ( select b.rro_account_number, b.name, b.payment_date, ufas.market, sum(u.gross_amount) market_total_amount, b.gross_amount total_payment_amount from ${schema}.df_usage u join ${schema}.df_usage_fas ufas on u.df_usage_uid = ufas.df_usage_fas_uid join ${schema}.df_usage_batch b on b.df_usage_batch_uid = u.df_usage_batch_uid where u.df_usage_batch_uid in
#{batchId}
group by rro_account_number, b.name, b.payment_date, ufas.market, b.gross_amount union all select b.rro_account_number, b.name, b.payment_date, ufas.market, sum(ua.gross_amount) market_total_amount, b.gross_amount total_payment_amount from ${schema}.df_usage_archive ua join ${schema}.df_usage_fas ufas on ua.df_usage_archive_uid = ufas.df_usage_fas_uid join ${schema}.df_usage_batch b on b.df_usage_batch_uid = ua.df_usage_batch_uid where ua.df_usage_batch_uid in
#{batchId}
group by rro_account_number, b.name, b.payment_date, ufas.market, b.gross_amount ) report left join ${schema}.df_rightsholder rro on rro.rh_account_number = report.rro_account_number group by report.rro_account_number, rro.name, report.name, report.payment_date, report.market, report.total_payment_amount order by report.payment_date desc, report.name

select nestedReport.rro_account_number, rh.name rro_name, nestedReport.payment_date, nestedReport.gross_amount_sent_to_lm, nestedReport.service_fee_amount_sent_to_lm, coalesce(nestedReport.service_fee_amount_sent_to_lm / nestedReport.gross_amount_sent_to_lm, 0) service_fee_sent_to_lm, nestedReport.net_amount_sent_to_lm, nestedReport.gross_amount_sent_to_lm * coalesce(fee.estimated_service_fee, #{defaultEstimatedServiceFee}) estimated_service_fee_amount_sent_to_lm, coalesce(fee.estimated_service_fee, #{defaultEstimatedServiceFee}) estimated_service_fee, nestedReport.gross_amount_sent_to_lm * coalesce(fee.estimated_service_fee, #{defaultEstimatedServiceFee}) - service_fee_amount_sent_to_lm service_fee_true_up, nestedReport.gross_amount_return_to_cla, nestedReport.gross_amount_return_to_cla * coalesce(fee.estimated_service_fee, #{defaultEstimatedServiceFee}) estimated_service_fee_amount_return_to_cla, nestedReport.service_fee_amount_return_to_cla, nestedReport.net_amount_return_to_cla, nestedReport.gross_amount_return_to_cla * coalesce(fee.estimated_service_fee, #{defaultEstimatedServiceFee}) - nestedReport.service_fee_amount_return_to_cla service_fee_true_up_return_to_cla from ( select nestedReport2.rro_account_number, nestedReport2.payment_date, sum(nestedReport2.gross_amount_sent_to_lm) gross_amount_sent_to_lm, sum(nestedReport2.service_fee_amount_sent_to_lm) service_fee_amount_sent_to_lm, sum(nestedReport2.net_amount_sent_to_lm) net_amount_sent_to_lm, sum(nestedReport2.gross_amount_return_to_cla) gross_amount_return_to_cla, sum(nestedReport2.service_fee_amount_return_to_cla) service_fee_amount_return_to_cla, sum(nestedReport2.net_amount_return_to_cla) net_amount_return_to_cla from ( select ua.product_family, ua.payee_account_number, batch.rro_account_number, batch.payment_date, sum(ua.gross_amount) gross_amount_sent_to_lm, sum(ua.service_fee_amount) service_fee_amount_sent_to_lm, sum(ua.net_amount) net_amount_sent_to_lm, case when (ua.product_family = #{productFamilyClaFas} and ua.payee_account_number = #{accountNumberClaFas}) then sum(ua.gross_amount) else 0 end gross_amount_return_to_cla, case when (ua.product_family = #{productFamilyClaFas} and ua.payee_account_number = #{accountNumberClaFas}) then sum(ua.service_fee_amount) else 0 end service_fee_amount_return_to_cla, case when (ua.product_family = #{productFamilyClaFas} and ua.payee_account_number = #{accountNumberClaFas}) then sum(ua.net_amount) else 0 end net_amount_return_to_cla from ${schema}.df_usage_batch batch join ${schema}.df_usage_archive ua on ua.df_usage_batch_uid = batch.df_usage_batch_uid and ua.status_ind = #{status} join ${schema}.df_scenario_audit sa on sa.df_scenario_uid = ua.df_scenario_uid and sa.created_datetime::date between #{fromDate} and #{toDate} and sa.action_type_ind = #{action} where batch.payment_date <= #{paymentDateTo} and batch.product_family in
#{productFamily}
group by batch.payment_date, batch.rro_account_number, ua.product_family, ua.payee_account_number ) as nestedReport2 group by nestedReport2.payment_date, nestedReport2.rro_account_number ) as nestedReport left join ${schema}.df_rro_estimated_service_fee_percentage fee on fee.rro_account_number = nestedReport.rro_account_number left join ${schema}.df_rightsholder rh on rh.rh_account_number = nestedReport.rro_account_number order by nestedReport.payment_date, rh.rh_account_number

select count(1)
from ${schema}.df_usage u
where df_scenario_uid = #{scenarioId}

select count(1)
from ${schema}.df_usage_archive ua
where df_scenario_uid = #{scenarioId}
