mapper.select:
select count(1)
from ${schema}.df_rightsholder_discrepancy
where df_scenario_uid = #{scenarioId}
    and status_ind = #{status}

select d.df_rightsholder_discrepancy_uid, d.old_rh_account_number, old_rh.name old_rightsholder_name, old_rh.df_rightsholder_uid old_rh_id, d.new_rh_account_number, new_rh.name new_rightsholder_name, new_rh.df_rightsholder_uid new_rh_id, d.wr_wrk_inst, d.work_title, d.product_family, d.created_datetime, d.updated_datetime, d.created_by_user, d.updated_by_user, d.record_version from ${schema}.df_rightsholder_discrepancy d left join ${schema}.df_rightsholder new_rh on new_rh.rh_account_number = d.new_rh_account_number left join ${schema}.df_rightsholder old_rh on old_rh.rh_account_number = d.old_rh_account_number where d.df_scenario_uid = #{scenarioId} and status_ind = #{status} order by
updated_datetime desc
limit #{pageable.limit} offset #{pageable.offset}

mapper.delete:
delete from ${schema}.df_rightsholder_discrepancy
where df_scenario_uid = #{scenarioId}
    and status_ind = #{status}
