mapper.select:
select s.df_scenario_uid, s.name, s.status_ind, s.description, s.created_by_user, s.created_datetime, s.updated_by_user, s.updated_datetime, f.product_family from ${schema}.df_scenario s join ${schema}.df_scenario_usage_filter f on s.df_scenario_uid = f.df_scenario_uid where f.product_family in
#{productFamily}
and s.status_ind in
#{status}
order by s.updated_datetime desc
