mapper.insert:
insert into ${schema}.df_udm_usage ( df_udm_usage_uid, df_udm_usage_batch_uid, original_detail_id, period_end_date, status_ind, wr_wrk_inst, reported_title, reported_standard_number, reported_pub_type, publication_format, article, language, company_id, survey_respondent, ip_address, survey_country, usage_date, survey_start_date, survey_end_date, reported_type_of_use, quantity, ineligible_reason,
) values ( #{id}, #{batchId}, #{originId}, #{periodEndDate}, #{status}, #{wrWrkInst}, #{reportedTitle}, #{reportedStandardNumber}, #{reportedPubType}, #{pubFormat}, #{article}, #{language}, #{companyId}, #{surveyRespondent}, #{ipAddress}, #{surveyCountry}, #{usageDate}, #{surveyStartDate}, #{surveyEndDate}, #{reportedTypeOfUse}, #{quantity}, #{ineligibleReason},
)

mapper.select:
select exists (
    select 1
    from ${schema}.df_udm_usage
    where original_detail_id = #{originalDetailId}
)

select df_udm_usage_uid, df_udm_usage_batch_uid, original_detail_id, period_end_date, status_ind, wr_wrk_inst, reported_title, reported_standard_number, reported_pub_type, publication_format, article, language, company_id, survey_respondent, ip_address, survey_country, usage_date, survey_start_date, survey_end_date, reported_type_of_use, quantity, ineligible_reason from ${schema}.df_udm_usage where df_udm_usage_uid in
#{usageId}
order by df_udm_usage_batch_uid
