mapper.delete:
with ua as (
    delete from ${schema}.df_usage_archive
    where df_usage_archive_uid in

        #{usageId}

)
delete from ${schema}.df_usage_fas
where df_usage_fas_uid in

    #{usageId}

mapper.sql:
select r.name rh_name, u.rh_account_number, p.name payee_name, u.payee_account_number, u.gross_total, u.service_fee_total, u.net_total, u.service_fee from ( select rh_account_number, payee_account_number, sum(gross_amount) gross_total, sum(service_fee_amount) service_fee_total, sum(net_amount) net_total, service_fee from ${schema}.df_usage_archive where df_scenario_uid = #{scenarioId} group by rh_account_number, payee_account_number, service_fee) u left join ${schema}.df_rightsholder r on u.rh_account_number = r.rh_account_number left join ${schema}.df_rightsholder p on u.payee_account_number = p.rh_account_number
where
limit #{pageable.limit} offset #{pageable.offset}

mapper.select:
select u.df_usage_archive_uid df_usage_uid, , u.check_number, u.check_date, u.ccc_event_id, u.distribution_name, u.distribution_date, u.period_end_date from ${schema}.df_usage_archive u join ${schema}.df_usage_fas ufas on u.df_usage_archive_uid = ufas.df_usage_fas_uid left join ${schema}.df_usage_batch ub on u.df_usage_batch_uid = ub.df_usage_batch_uid left join ${schema}.df_rightsholder rh on rh.rh_account_number = u.rh_account_number left join ${schema}.df_rightsholder rro on rro.rh_account_number = ub.rro_account_number left join ${schema}.df_rightsholder p on p.rh_account_number = u.payee_account_number where u.df_scenario_uid = #{scenarioId} and u.rh_account_number = #{accountNumber}
and (
)
order by
, df_usage_archive_uid
df_usage_archive_uid

select count(1) from ${schema}.df_usage_archive u left join ${schema}.df_usage_batch ub on u.df_usage_batch_uid = ub.df_usage_batch_uid left join ${schema}.df_rightsholder rro on ub.rro_account_number = rro.rh_account_number where df_scenario_uid = #{scenarioId} and u.rh_account_number = #{accountNumber}
and (
)

select u.df_usage_archive_uid df_usage_uid,
from ${schema}.df_usage_archive u join ${schema}.df_usage_batch ub on u.df_usage_batch_uid = ub.df_usage_batch_uid join ${schema}.df_usage_aacl uaacl on u.df_usage_archive_uid = uaacl.df_usage_aacl_uid left join ${schema}.df_publication_type pt on uaacl.df_publication_type_uid = pt.df_publication_type_uid left join ${schema}.df_detail_licensee_class dlc on uaacl.detail_licensee_class_id = dlc.detail_licensee_class_id left join ${schema}.df_rightsholder rh on rh.rh_account_number = u.rh_account_number where u.df_usage_archive_uid in
#{usageId}

mapper.insert:
with result as ( insert into ${schema}.df_usage_archive ( df_usage_archive_uid, , , check_number, check_date, ccc_event_id, distribution_name, distribution_date, period_end_date, lm_detail_id, ) values ( #{id}, #{batchId}, #{scenarioId}, #{wrWrkInst}, #{workTitle}, #{systemTitle}, #{rightsholder.accountNumber}, #{status}, #{productFamily}, #{standardNumber}, #{standardNumberType}, #{numberOfCopies}, #{grossAmount}, #{comment}, #{serviceFee}, #{serviceFeeAmount}, #{netAmount}, #{payee.accountNumber}, #{checkNumber}, #{checkDate}, #{cccEventId}, #{distributionName}, #{distributionDate}, #{periodEndDate}, #{lmDetailId},
) ) insert into ${schema}.df_usage_sal ( df_usage_sal_uid, assessment_name, assessment_type, coverage_year, scored_assessment_date, question_identifier, states, number_of_views, grade, grade_group, reported_work_portion_id, reported_standard_number, reported_media_type, media_type_weight, reported_article, reported_author, reported_publisher, reported_publication_date, reported_page_range, reported_vol_number_series, detail_type,
) values ( #{id}, #{salUsage.assessmentName}, #{salUsage.assessmentType}, #{salUsage.coverageYear}, #{salUsage.scoredAssessmentDate}, #{salUsage.questionIdentifier}, #{salUsage.states}, #{salUsage.numberOfViews}, #{salUsage.grade}, #{salUsage.gradeGroup}, #{salUsage.reportedWorkPortionId}, #{salUsage.reportedStandardNumber}, #{salUsage.reportedMediaType}, #{salUsage.mediaTypeWeight}, #{salUsage.reportedArticle}, #{salUsage.reportedAuthor}, #{salUsage.reportedPublisher}, #{salUsage.reportedPublicationDate}, #{salUsage.reportedPageRange}, #{salUsage.reportedVolNumberSeries}, #{salUsage.detailType}, )

mapper.select:
select u.df_usage_archive_uid df_usage_uid, u.payee_account_number, p.name payee_name, u.gross_amount, u.service_fee_amount, u.net_amount, u.service_fee,
from ${schema}.df_usage_archive u join ${schema}.df_usage_batch ub on u.df_usage_batch_uid = ub.df_usage_batch_uid join ${schema}.df_usage_sal usal on u.df_usage_archive_uid = usal.df_usage_sal_uid left join ${schema}.df_rightsholder rh on rh.rh_account_number = u.rh_account_number left join ${schema}.df_rightsholder p on p.rh_account_number = u.payee_account_number where u.df_usage_archive_uid in
#{usageId}
