mapper.insert:
insert into ${schema}.df_usage_batch( df_usage_batch_uid, name, rro_account_number, product_family, payment_date, fiscal_year, gross_amount, nts_fields, sal_fields, baseline_years,
) values ( #{id}, #{name}, #{rro.accountNumber}, #{productFamily}, #{paymentDate}, #{fiscalYear}, #{grossAmount}, #{ntsFields, typeHandler=NtsBatchFieldsTypeHandler}, #{salFields, typeHandler=SalBatchFieldsTypeHandler}, #{numberOfBaselineYears},
)

mapper.sql:
where u.df_usage_batch_uid = ub.df_usage_batch_uid
    and u.status_ind = #{status}
    and ufas.df_fund_pool_uid is null

mapper.select:
select ub.df_usage_batch_uid, ub.name, ( select sum(u.gross_amount) from ${schema}.df_usage u join ${schema}.df_usage_fas ufas on u.df_usage_uid = ufas.df_usage_fas_uid
) gross_amount, ub.updated_datetime from ${schema}.df_usage_batch ub where ub.product_family in
#{productFamily}
and exists ( select 1 from ${schema}.df_usage u join ${schema}.df_usage_fas ufas on u.df_usage_uid = ufas.df_usage_fas_uid
) order by ub.updated_datetime desc

select name from ${schema}.df_usage_batch ub where ub.df_usage_batch_uid in
#{batchId}
and exists ( select 1 from ${schema}.df_usage u where u.df_usage_batch_uid = ub.df_usage_batch_uid and u.status_ind not in
#{status}
) order by ub.updated_datetime desc

select distinct ub.name
from ${schema}.df_usage u
join ${schema}.df_usage_batch ub on ub.df_usage_batch_uid = u.df_usage_batch_uid
where ub.product_family in

        #{productFamily}

    and u.status_ind = #{status}
