mapper.insert:
insert into ${schema}.df_usage_batch( df_usage_batch_uid, name, rro_account_number, product_family, payment_date, fiscal_year, gross_amount, fund_pool,
) values ( #{id}, #{name}, #{rro.accountNumber}, #{productFamily}, #{paymentDate}, #{fiscalYear}, #{grossAmount}, #{fundPool, typeHandler=FundPoolTypeHandler},
)

mapper.sql:
where u.df_usage_batch_uid = ub.df_usage_batch_uid
    and u.status_ind = #{status}
    and u.df_fund_pool_uid is null

mapper.select:
select ub.df_usage_batch_uid, ub.name, ( select sum(u.gross_amount) from ${schema}.df_usage u
) gross_amount, ub.updated_datetime from ${schema}.df_usage_batch ub where ub.product_family in
#{productFamily}
and exists ( select 1 from ${schema}.df_usage u
) order by ub.updated_datetime desc
