mapper.select:
select count(1)
from ${schema}.df_usage u
where df_scenario_uid = #{scenarioId}

select u.df_usage_uid, u.df_usage_batch_uid, u.df_scenario_uid, u.df_fund_pool_uid, u.wr_wrk_inst, u.work_title, u.system_title, u.rh_account_number, r.name rh_name, r.df_rightsholder_uid rh_id, u.payee_account_number, p.name payee_name, p.df_rightsholder_uid payee_id, u.status_ind, u.product_family, u.article, u.standard_number, u.standard_number_type, u.publisher, u.publication_date, u.market, u.market_period_from, u.market_period_to, u.author, u.comment, u.number_of_copies, u.reported_value, u.gross_amount, u.service_fee_amount, u.service_fee, u.net_amount, u.is_rh_participating_flag, u.created_datetime, u.updated_datetime, u.created_by_user, u.updated_by_user, u.record_version from ${schema}.df_usage u left join ${schema}.df_rightsholder r on r.rh_account_number = u.rh_account_number left join ${schema}.df_rightsholder p on p.rh_account_number = u.payee_account_number where u.df_usage_uid in
#{usageId}
order by u.df_usage_uid

select count(1)
from ${schema}.df_usage
where status_ind = #{status}
    and wr_wrk_inst in

