mapper.update:
update ${schema}.df_usage set status_ind = #{status}, rh_account_number = #{rhAccountNumber},
where df_usage_uid in
#{usageId}

mapper.select:
select reportInfo.rro_account_number, rh.name rro_name, reportInfo.payment_date, reportInfo.usages_gross_amount, reportInfo.usages_gross_amount * reportInfo.estimated_service_fee estimated_service_fee_amount, reportInfo.usages_gross_amount - (reportInfo.usages_gross_amount * reportInfo.estimated_service_fee) estimated_net_amount, reportInfo.estimated_service_fee, reportInfo.withdrawn_gross_amount from (select batch.rro_account_number, batch.payment_date, coalesce(sum(u.gross_amount), 0) usages_gross_amount, coalesce(fee.estimated_service_fee, 0.00000) estimated_service_fee, coalesce(sum(u.withdrawn_gross_amount), 0) withdrawn_gross_amount from ${schema}.df_usage_batch batch left join ${schema}.df_rro_estimated_service_fee_percentage fee on fee.rro_account_number = batch.rro_account_number left join ( select df_usage_batch_uid, gross_amount, case when status_ind in
#{withdrawnStatus}
then gross_amount else 0 end withdrawn_gross_amount from ${schema}.df_usage ) u on u.df_usage_batch_uid = batch.df_usage_batch_uid where batch.payment_date <= #{paymentDate} group by payment_date, batch.rro_account_number, fee.estimated_service_fee) as reportInfo left join ${schema}.df_rightsholder rh on rh.rh_account_number = reportInfo.rro_account_number where reportInfo.usages_gross_amount != 0 order by reportInfo.rro_account_number, reportInfo.payment_date

select report.rro_account_number, rro.name rro_name, report.name batch_name, report.payment_date, report.market, sum(report.market_total_amount) market_total_amount, report.total_payment_amount from ( select b.rro_account_number, b.name, b.payment_date, market, sum(u.gross_amount) market_total_amount, b.gross_amount total_payment_amount from ${schema}.df_usage u join ${schema}.df_usage_batch b on b.df_usage_batch_uid = u.df_usage_batch_uid where u.df_usage_batch_uid in
#{batchId}
group by rro_account_number, b.name, b.payment_date, market, b.gross_amount union all select b.rro_account_number, b.name, b.payment_date, market, sum(ua.gross_amount) market_total_amount, b.gross_amount total_payment_amount from ${schema}.df_usage_archive ua join ${schema}.df_usage_batch b on b.df_usage_batch_uid = ua.df_usage_batch_uid where ua.df_usage_batch_uid in
#{batchId}
group by rro_account_number, b.name, b.payment_date, market, b.gross_amount ) report left join ${schema}.df_rightsholder rro on rro.rh_account_number = report.rro_account_number group by report.rro_account_number, rro.name, report.name, report.payment_date, report.market, report.total_payment_amount order by report.payment_date desc, report.name

mapper.update:
update ${schema}.df_usage set status_ind = #{statusTo}, df_fund_pool_uid = #{fundPoolId},
where df_usage_batch_uid in
#{batchId}
and status_ind = #{statusFrom}
