mapper.select:
select df_usage_uid
from ${schema}.df_usage
where status_ind = #{status}
    and product_family = #{productFamily}
order by df_usage_uid

select u.df_usage_uid, u.df_usage_batch_uid, u.df_scenario_uid, ufas.df_fund_pool_uid, u.wr_wrk_inst, u.work_title, u.system_title, u.rh_account_number, r.name rh_name, r.df_rightsholder_uid rh_id, u.payee_account_number, p.name payee_name, p.df_rightsholder_uid payee_id, u.status_ind, u.product_family, ufas.article, u.standard_number, u.standard_number_type, ufas.publisher, ufas.publication_date, ufas.market, ufas.market_period_from, ufas.market_period_to, ufas.author, u.comment, u.number_of_copies, ufas.reported_value, u.gross_amount, u.service_fee_amount, u.service_fee, u.net_amount, ufas.is_rh_participating_flag, ufas.is_payee_participating_flag, u.created_datetime, u.updated_datetime, u.created_by_user, u.updated_by_user, u.record_version from ${schema}.df_usage u join ${schema}.df_usage_fas ufas on u.df_usage_uid = ufas.df_usage_fas_uid left join ${schema}.df_rightsholder r on r.rh_account_number = u.rh_account_number left join ${schema}.df_rightsholder p on p.rh_account_number = u.payee_account_number where u.df_usage_uid in
#{usageId}
order by u.df_usage_uid

mapper.delete:
with u as (
    delete from ${schema}.df_usage
    where df_usage_uid = #{usageId}
)
delete from ${schema}.df_usage_fas
where df_usage_fas_uid = #{usageId}

mapper.update:
with u as ( update ${schema}.df_usage set status_ind = #{status}, df_scenario_uid = #{scenarioId}, payee_account_number = #{payee.accountNumber}, net_amount = #{netAmount}, service_fee_amount = #{serviceFeeAmount}, service_fee = #{serviceFee},
where df_usage_uid = #{id} ) update ${schema}.df_usage_fas set is_rh_participating_flag = #{rhParticipating}, is_payee_participating_flag = #{payeeParticipating},
where df_usage_fas_uid = #{id}

with result as (

    where df_usage_uid = #{usageId}
)
update ${schema}.df_usage_fas
set
    is_rh_participating_flag = false,
    is_payee_participating_flag = false,

where df_usage_fas_uid = #{usageId}

mapper.select:
select count(1)
    from (
        select df_usage_uid
        from ${schema}.df_usage
        where df_usage_uid = #{usageId}
            and status_ind = #{status}
    union
        select df_usage_archive_uid
        from ${schema}.df_usage_archive
        where df_usage_archive_uid = #{usageId}
            and status_ind = #{status}
    ) as usage_uid;

select p.name payee_name, u.payee_account_number, u.gross_total, u.service_fee_total, u.net_total, u.is_payee_participating_flag from ( select u.payee_account_number, sum(u.gross_amount) gross_total, sum(u.service_fee_amount) service_fee_total, sum(u.net_amount) net_total, ufas.is_payee_participating_flag from ${schema}.df_usage u join ${schema}.df_usage_fas ufas on u.df_usage_uid = ufas.df_usage_fas_uid join ${schema}.df_rightsholder payee on u.payee_account_number = payee.rh_account_number join ${schema}.df_scenario s on u.df_scenario_uid = s.df_scenario_uid where s.status_ind = #{scenarioStatus}
and u.df_scenario_uid in
#{scenarioId}
and ufas.is_payee_participating_flag = ${filter.payeeParticipating}
and (payee.name ilike '%' || #{filter.searchValue} || '%' or cast(u.payee_account_number as text) like '%' || #{filter.searchValue} || '%')
group by payee_account_number, ufas.is_payee_participating_flag
having sum(u.net_amount) < ${filter.netAmountMinThreshold}
) u left join ${schema}.df_rightsholder p on u.payee_account_number = p.rh_account_number order by p.name asc

