with u as ( update ${schema}.df_usage set status_ind = #{status}, wr_wrk_inst = #{wrWrkInst}, work_title = #{workTitle}, system_title = #{systemTitle}, rh_account_number = #{rightsholder.accountNumber}, payee_account_number = #{payee.accountNumber}, net_amount = #{netAmount}, service_fee_amount = #{serviceFeeAmount}, service_fee = #{serviceFee},
where df_usage_uid = #{id} ) update ${schema}.df_usage_fas set is_rh_participating_flag = #{rhParticipating}, is_payee_participating_flag = #{payeeParticipating},
