mapper.select:
select
    (select count(1)
    from ${schema}.df_usage_fas
    where df_usage_fas_uid in

        #{usageId}
    ) +
    (select count(1)
    from ${schema}.df_usage_aacl
    where df_usage_aacl_uid in

        #{usageId}
    )

select rh.df_rightsholder_uid rh_uid, rh.name rh_name, u.rh_account_number, payee.df_rightsholder_uid payee_uid, payee.name payee_name, u.payee_account_number, u.product_family from ( select rh_account_number, payee_account_number, product_family from ${schema}.df_usage u where df_scenario_uid in
#{scenarioId}
group by u.rh_account_number, u.payee_account_number, u.product_family ) u left join ${schema}.df_rightsholder rh on u.rh_account_number = rh.rh_account_number left join ${schema}.df_rightsholder payee on u.payee_account_number = payee.rh_account_number
